<?php  namespace Sabberworm\CSS\Property; class Charset implements AtRule { private $sCharset; public function __construct($sCharset) { $this->sCharset = $sCharset; } public function setCharset($sCharset) { $this->sCharset = $sCharset; } public function getCharset() { return $this->sCharset; } public function __toString() { return "@charset {$this->sCharset->__toString()};"; } public function atRuleName() { return 'charset'; } public function atRuleArgs() { return $this->sCharset; } }